    "HOTEL",
];

export const LOCATION_TYPE = [
    "Point"
];

export const MEDIA_CATEGORY = [
    "COMMON", 
    "CATEGORY", 
    "PRODUCT", 
    "OFFER", 
    "COMBO",
    "SHOP"
];

