import mongoose, { Schema } from 'mongoose';
import { STATUS } from "../../services/constant/options";
import { offerSchema } from '../offer/model';

//--------------------------------------------------------------//
//   Brands Schema                                              //
//--------------------------------------------------------------//

export const brands = {
  name: {
    type: String,
    required: [true, 'Brand name required!']
  },
  image: {
    type: String,
  },
  model: {
    type: String
  },
  offers: [ offerSchema ],
  status: {
    type: String, 
    enum: STATUS,
    required: [true, 'Brand status required!']
  },
};

